INSERT INTO `config` VALUES (1, 'SEO', '$URLMethod', '1', '1', '1 or 2', 'URL method', 'Search engine friendly URLs<br><b>1</b> = Non-SEO Links. Example: /story.php?title=Example-Title<br><b>2</b> SEO Method. Example: /category/example-title/ method.<br /><strong>Note:</strong> You must rename htaccess.default to .htaccess AND add code found at the bottom of the Admin > Manage > Categories page to the .htaccess file', 'normal', NULL);
INSERT INTO `config` VALUES (2, 'SEO', 'enable_friendly_urls', 'true', 'true', 'true / false', 'Friendly URL''s for stories', 'Use the story title in the url by setting this value to true. Example: /story/(story title)/<br />Keep this setting as TRUE if using URL Method 1', 'define', NULL);
INSERT INTO `config` VALUES (4, 'Voting', 'votes_to_publish', '5', '5', 'number', 'Votes to publish', 'Number of votes before story is sent to the front page.', 'define', NULL);
INSERT INTO `config` VALUES (5, 'Voting', 'days_to_publish', '10', '10', 'number', 'Days to publish', 'After this many days posts will not be eligible to move from upcoming to published pages', 'define', NULL);
INSERT INTO `config` VALUES (6, 'Misc', '$trackbackURL', 'pligg.com', 'pligg.com', 'pligg.com', 'Trackback URL', 'The url to be used in <a href="http://en.wikipedia.org/wiki/Trackback">trackbacks</a>.', 'normal', '"');
INSERT INTO `config` VALUES (7, 'Location Installed', '$my_base_url', 'http://localhost', 'http://localhost', 'http://(your site name)(no trailing /)', 'Base URL', '<br>\r\n<b>Examples</b>\r\n<br>\r\nhttp://demo.pligg.com<br>\r\nhttp://localhost<br>\r\nhttp://www.pligg.com', 'normal', '''');
INSERT INTO `config` VALUES (8, 'Location Installed', '$my_pligg_base', '', '', '/(folder name)', 'Pligg Base Folder', '<br>\r\n<b>Examples</b>\r\n<br>\r\n/pligg -- if installed in the /pligg subfolder<br>\r\nLeave blank if installed in the site root folder.', 'normal', '''');
INSERT INTO `config` VALUES (9, 'Tags', 'Enable_Tags', 'true', 'true', 'true / false', 'Enable Tags', 'Enable the use of tags and the tag cloud.', 'define', NULL);
INSERT INTO `config` VALUES (10, 'Tags', '$tags_min_pts', '8', '8', 'number (should be at least 8)', 'Tag Minimum Font Size', '<b>Only used if Tags are enabled.</b> How small should the text for the smallest tags be.', 'normal', NULL);
INSERT INTO `config` VALUES (11, 'Tags', '$tags_max_pts', '36', '36', 'number', 'Tags Maximum Font Size', '<b>Only used if Tags are enabled.</b> How large should the text for the largest tags be.', 'normal', NULL);
INSERT INTO `config` VALUES (12, 'Tags', '$tags_words_limit', '100', '100', 'number', 'Tag Cloud Word Limit', '<b>Only used if Tags are enabled.</b> The most tags to show in the cloud.', 'normal', NULL);
INSERT INTO `config` VALUES (13, 'AntiSpam', 'CHECK_SPAM', 'false', 'false', 'true / false', 'Enable spam checking', 'Checks submitted domains to see if they''re on a blacklist.', 'define', NULL);
INSERT INTO `config` VALUES (14, 'AntiSpam', '$MAIN_SPAM_RULESET', 'antispam.txt', 'antispam.txt', 'text file', 'Main Spam Ruleset', 'What file should be used to check for spam domains?', 'normal', '"');
INSERT INTO `config` VALUES (15, 'AntiSpam', '$USER_SPAM_RULESET', 'local-antispam.txt', 'local-antispam.txt', 'text file', 'Local Spam Ruleset', 'What file should Pligg write to if you mark items as spam?', 'normal', '"');
INSERT INTO `config` VALUES (16, 'AntiSpam', '$SPAM_LOG_BOOK', 'spamlog.log', 'spamlog.log', 'text file', 'Spam Log', 'File to log spam blocks to.', 'normal', '"');
INSERT INTO `config` VALUES (17, 'Live', 'Enable_Live', 'true', 'true', 'true / false', 'Enable Live', 'Enable the live page.', 'define', NULL);
INSERT INTO `config` VALUES (18, 'Live', 'items_to_show', '20', '20', 'number', 'Live Items to Show', 'Number of items to show on the live page.', 'define', NULL);
INSERT INTO `config` VALUES (19, 'Live', 'how_often_refresh', '20', '20', 'number', 'How often to refresh', 'How many seconds between refreshes - not recommended to set it less than 5.', 'define', NULL);
INSERT INTO `config` VALUES (20, 'Submit', 'Story_Content_Tags_To_Allow_Normal', '', '', 'HTML tags', 'HTML tags to allow to Normal users', 'leave blank to not allow tags. Examples are \"&lt;strong&gt;&lt;br&gt;&lt;font&gt;&lt;img&gt;&lt;p&gt;\"', 'define', '"');
INSERT INTO `config` VALUES (21, 'Submit', 'Submit_Require_A_URL', 'true', 'true', 'true / false', 'Require a URL when Submitting', 'Require a URL when submitting.', 'define', NULL);
INSERT INTO `config` VALUES (22, 'Submit', 'Submit_Show_URL_Input', 'true', 'true', 'true / false', 'Show the URL Input Box', 'Show the URL input box in submit step 1.', 'define', NULL);
INSERT INTO `config` VALUES (23, 'Submit', 'No_URL_Name', 'Editorial', 'Editorial', 'text', 'No URL text', 'Label to show when there is no URL provided in submit step 1.', 'define', '''');
INSERT INTO `config` VALUES (27, 'Avatars', 'Default_Gravatar_Large', '/avatars/Gravatar_30.gif', '/avatars/Gravatar_30.gif', 'Path to image', 'Default  avatar (large)', 'Default location of large gravatar.', 'define', '''');
INSERT INTO `config` VALUES (28, 'Avatars', 'Default_Gravatar_Small', '/avatars/Gravatar_15.gif', '/avatars/Gravatar_15.gif', 'Path to image', 'Default avatar (small)', 'Default location of small gravatar.', 'define', '''');
INSERT INTO `config` VALUES (29, 'Misc', 'Enable_Extra_Fields', 'false', 'false', 'true / false', 'Enable extra fields', 'Enable extra fields when submittng stories?', 'define', NULL);
INSERT INTO `config` VALUES (30, 'Comments', 'Enable_Comment_Voting', 'true', 'true', 'true / false', 'Comment Voting', 'Allow users to vote on comments?', 'define', NULL);
INSERT INTO `config` VALUES (31, 'Comments', '$CommentOrder', '4', '4', '1 - 4', 'Comment Sort Order', '<br><b>1</b> = Top rated comments first, newest on top\r\n<br><b>2</b> = Newest comments first	\r\n<br><b>3</b> = Top rated comments first, oldest on top\r\n<br><b>4</b> = Oldest comments first', 'normal', NULL);
INSERT INTO `config` VALUES (33, 'Misc', 'Allow_Friends', 'true', 'true', 'true / false', 'Allow friends', 'Allow adding, removing, and viewing friends.', 'define', NULL);
INSERT INTO `config` VALUES (34, 'Voting', 'Voting_Method', '1', '1', '1-3', 'Voting method', '<b>1</b> = the digg method. <b>2</b> = 5 star rating method. <b>3</b> = Karma method', 'define', NULL);
INSERT INTO `config` VALUES (36, 'Tell-a-Friend', 'Enable_Recommend', 'true', 'true', 'true / false', 'Enable tell-a-friend', 'Enable or disable the tell-a-friend link for each story.', 'define', NULL);
INSERT INTO `config` VALUES (37, 'Tell-a-Friend', 'Email_Subject', 'Pligg.com story: ', 'Pligg.com story: ', 'text', 'Email Subject Prefix', 'The prefix added to the email title. The story title will be added to the subject of the email.', 'define', '"');
INSERT INTO `config` VALUES (38, 'Tell-a-Friend', 'Default_Message', 'I thought you might like to see this.', 'I thought you might like to see this.', 'text', 'Default message', 'Message sent along with story description in email.', 'define', '"');
INSERT INTO `config` VALUES (39, 'Tell-a-Friend', 'Included_Text_Part1', 'Pligg.com user ', 'Pligg.com user ', 'text file', 'Who Sent', 'The text used before displaying the username who sent the article.', 'define', '"');
INSERT INTO `config` VALUES (40, 'Tell-a-Friend', 'Included_Text_Part2', ' would like to share this story with you: ', ' would like to share this story with you: ', 'Text', 'Who Sent 2', 'What appears after the user name. <b>Keep a space in the beginning and end.</b>', 'define', '"');
INSERT INTO `config` VALUES (41, 'Tell-a-Friend', 'Send_From_Email', 'noreply@pligg.com', 'noreply@pligg.com', 'email address', 'Sent from email', 'Email address email is sent from.', 'define', '"');
INSERT INTO `config` VALUES (43, 'Misc', 'SearchMethod', '3', '3', '1 - 3', 'Search method', '<br><b>1</b> = uses MySQL MATCH for FULLTEXT indexes (or something). <b>Problems are MySQL STOP words and words less than 4 characters. Note: these limitations do not affect clicking on a TAG to search by it.</b>\r\n<br><b>2</b> = uses MySQL LIKE and is much slower, but returns better results. Also supports "*" and "-"\r\n<br><b>3</b> = is a hybrid, using method 1 if possible, but method 2 if needed.', 'define', NULL);
INSERT INTO `config` VALUES (45, 'Anonymous', 'anonymous_vote', 'false', 'false', 'true / false', 'Anonymous vote', 'Allow anonymous users to vote on articles.', 'define', '"');
INSERT INTO `config` VALUES (46, 'Anonymous', '$anon_karma', '1', '1', 'number', 'Anonymous Karma', 'Karma is an experimental feature that measures user activity and reputation.', 'normal', NULL);
INSERT INTO `config` VALUES (47, 'Hidden', 'SALT_LENGTH', '9', '9', 'number', 'SALT_LENGTH', 'SALT_LENGTH', 'define', NULL);
